/* Clean up the header for developer.mozilla.org.   Cameron Kaiser   Public domain. */function() {	return {		wantURI : function(uri) {			return true;		},		parseHTML : function(text) {			var answer;						// Remove the top header.			answer = text.replace(/<\/?header[^>]+>/mig, "");			// Remove the mozilla-tab.			answer = answer.replace(/<a id="moz-tab"/, "<a");			// Remove the stupid JavaScript whine.			answer = answer.replace(/<div class="noscript">.+<\/div><\/noscript>/, "</noscript>");			// Remove the styling from the h1 title.			answer = answer.replace(/<h1 id="logo">/,				"<h1 style=\"color: #404040\">");			// Remove the CSS rollovers; they don't work. [\s\S]+? matches all characters,			// including newlines; +? is a non-greedy match a la Perl.			answer = answer.replace(/<nav id="nav"[\s\S]+?<\/nav>/m, "");						return {				response : "ok",				body     : answer			};		}	};};