/* Stele for yfrog.com   Cameron Kaiser   Public domain */function() {	return {		wantURI : function(uri) {			if (uri.path == "/" || uri.path == "")				return false;			return true;		},		parseHTML : function(text) {			// Just pull out the image and selectors (essentially, return everything after).			var img = '';						var pat = /(<div class="main-image[\s\S]+)/mi;			var result = text.match(pat);			if (result && result.length && result.length > 1)				img = result[1]; // result[0] is the entire text			return {				response : "ok",				body     : ((img.length && img.length > 0) ?					"<html><head><title>yfrog translated by Byblos</title></head><body>"+img					: "oops "+result)			};		}	};};